mapper.delete:
DELETE
FROM favorite
WHERE member_id = #{memberId}
  AND estate_id = #{estateId}

mapper.select:
SELECT fid, member_id AS memberId, estate_id AS estateId, created_at AS createdAt, updated_at AS updatedAt
FROM favorite
WHERE member_id = #{memberId}

SELECT fid, member_id AS memberId, estate_id AS estateId, created_at AS createdAt, updated_at AS updatedAt
FROM favorite
WHERE member_id = #{memberId}
  AND estate_id = #{estateId}
