mapper.select:
SELECT mid, phone_num, email, last_login, created_at, updated_at
FROM member
WHERE mid = #{memberId}

mapper.update:
UPDATE member
SET phone_num = #{request.phoneNum}, name = #{request.name}
WHERE mid = #{memberId} AND password = #{request.password}

UPDATE member
SET password = #{newPassword},
    salt     = #{salt}
WHERE mid = #{memberId}
